// Schedule View Component
const ScheduleView = ({ appointments, leads }) => {
  const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const currentDate = new Date();
  const currentMonth = currentDate.getMonth();
  const currentYear = currentDate.getFullYear();

  // Generate calendar days
  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const days = [];

    // Add empty cells for days before month starts
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }

    // Add days of the month
    for (let i = 1; i <= daysInMonth; i++) {
      days.push(i);
    }

    return days;
  };

  const calendarDays = getDaysInMonth(currentDate);

  return (
    <div className="space-y-6">
      <h2 className="text-3xl font-bold text-gray-800">Appointment Schedule</h2>

      <div className="bg-white rounded-lg shadow p-6">
        <div className="mb-4">
          <h3 className="text-xl font-semibold text-gray-800">
            {currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
          </h3>
        </div>

        <div className="grid grid-cols-7 gap-1">
          {days.map(day => (
            <div key={day} className="text-center text-sm font-semibold text-gray-600 p-2">
              {day}
            </div>
          ))}
          {calendarDays.map((day, index) => (
            <div
              key={index}
              className={`border border-gray-200 p-2 h-24 ${
                day === currentDate.getDate() ? 'bg-indigo-50 border-indigo-500' : ''
              } ${day ? 'hover:bg-gray-50' : ''}`}
            >
              {day && (
                <div>
                  <div className="font-semibold text-sm mb-1">{day}</div>
                  <div className="text-xs space-y-1">
                    {appointments
                      .filter(apt => {
                        const aptDate = new Date(apt.date);
                        return aptDate.getDate() === day && 
                               aptDate.getMonth() === currentMonth &&
                               aptDate.getFullYear() === currentYear;
                      })
                      .slice(0, 2)
                      .map(apt => {
                        const lead = leads.find(l => l.id === apt.leadId);
                        return (
                          <div key={apt.id} className="bg-indigo-100 text-indigo-700 px-1 rounded truncate">
                            {apt.time} - {lead?.customerName}
                          </div>
                        );
                      })}
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Today's Detailed Schedule */}
      <div className="bg-white rounded-lg shadow p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4">Today's Appointments</h3>
        <div className="space-y-3">
          {appointments.filter(apt => apt.date === currentDate.toISOString().split('T')[0]).map(apt => {
            const lead = leads.find(l => l.id === apt.leadId);
            return (
              <div key={apt.id} className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50">
                <div className="flex justify-between items-start">
                  <div>
                    <div className="flex items-center space-x-2">
                      <Calendar className="h-5 w-5 text-indigo-500" />
                      <span className="font-semibold text-lg">{apt.time}</span>
                      <span className="text-sm text-gray-500">- {apt.type}</span>
                    </div>
                    <h4 className="font-semibold mt-2">{lead?.customerName}</h4>
                    <p className="text-sm text-gray-600">{lead?.address}</p>
                    <div className="flex items-center space-x-4 mt-2">
                      <span className="text-sm text-gray-500 flex items-center">
                        <Phone className="h-4 w-4 mr-1" /> {lead?.phoneNumber}
                      </span>
                      <span className="text-sm text-gray-500 flex items-center">
                        <Mail className="h-4 w-4 mr-1" /> {lead?.email}
                      </span>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="font-semibold text-green-600">{lead?.dabellaQuote}</p>
                    <QualityBadge quality={lead?.quality} />
                  </div>
                </div>
                {lead?.notes && (
                  <div className="mt-3 p-3 bg-gray-50 rounded text-sm text-gray-600">
                    <strong>Notes:</strong> {lead.notes}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

// Route Map View Component
const RouteMapView = ({ leads }) => {
  const [selectedArea, setSelectedArea] = useState('all');

  // Group leads by area (simplified - in real app would use geocoding)
  const groupedLeads = leads.reduce((acc, lead) => {
    // Extract city from address (simplified)
    const city = lead.address.split(',')[1]?.trim() || 'Unknown';
    if (!acc[city]) acc[city] = [];
    acc[city].push(lead);
    return acc;
  }, {});

  const getFilteredLeads = () => {
    if (selectedArea === 'all') return leads;
    return groupedLeads[selectedArea] || [];
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-3xl font-bold text-gray-800">Route Planning</h2>
