              <div className="flex-grow">
                <div className="flex justify-between items-start">
                  <div>
                    <p className="font-semibold">{lead.customerName}</p>
                    <p className="text-sm text-gray-600">{lead.address}</p>
                    <div className="flex items-center space-x-4 mt-1">
                      <span className="text-xs text-gray-500">{lead.phoneNumber}</span>
                      <QualityBadge quality={lead.quality} size="small" />
                      <span className="text-xs text-gray-500">{lead.disposition}</span>
                    </div>
                  </div>
                  <div className="flex space-x-2">
                    <button className="text-indigo-600 hover:text-indigo-800">
                      <Navigation className="h-4 w-4" />
                    </button>
                    <button className="text-green-600 hover:text-green-800">
                      <Phone className="h-4 w-4" />
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Route Stats */}
        <div className="mt-6 p-4 bg-gray-50 rounded-lg">
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <p className="text-2xl font-bold text-indigo-600">{getFilteredLeads().length}</p>
              <p className="text-sm text-gray-600">Total Stops</p>
            </div>
            <div>
              <p className="text-2xl font-bold text-green-600">~{getFilteredLeads().length * 0.3}mi</p>
              <p className="text-sm text-gray-600">Est. Distance</p>
            </div>
            <div>
              <p className="text-2xl font-bold text-purple-600">~{getFilteredLeads().length * 15}min</p>
              <p className="text-sm text-gray-600">Est. Time</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Helper Components
const NavButton = ({ icon, label, isActive, onClick }) => (
  <button
    onClick={onClick}
    className={`flex items-center space-x-2 px-4 py-2 rounded-lg font-medium transition duration-200 ${
      isActive
        ? 'bg-indigo-100 text-indigo-700'
        : 'text-gray-600 hover:bg-gray-100'
    }`}
  >
    {icon}
    <span>{label}</span>
  </button>
);

const StatCard = ({ title, value, icon, color }) => (
  <div className="bg-white rounded-lg shadow p-6">
    <div className="flex items-center justify-between">
      <div>
        <p className="text-sm text-gray-600">{title}</p>
        <p className="text-2xl font-bold text-gray-800 mt-1">{value}</p>
      </div>
      <div className={`${color} text-white p-3 rounded-lg`}>
        {icon}
      </div>
    </div>
  </div>
);

const QualityBadge = ({ quality, size = 'normal' }) => {
  const colors = {
    Hot: 'bg-red-100 text-red-800 border-red-200',
    Warm: 'bg-yellow-100 text-yellow-800 border-yellow-200',
    Cold: 'bg-blue-100 text-blue-800 border-blue-200'
  };

  const sizeClasses = size === 'small' ? 'text-xs px-1.5 py-0.5' : 'text-sm px-2 py-1';

  return (
    <span className={`${sizeClasses} inline-flex font-semibold rounded-full border ${colors[quality]}`}>
      {quality}
    </span>
  );
};

export default App;
